import React from 'react';
import { View, Text, Image, TouchableOpacity, ScrollView } from 'react-native';

const images = [
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg',
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg',
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg',
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg'
];

function Home() {
	return (
		<View style={{ backgroundColor: 'white', flex: 1 }}>
			<View style={{ justifyContent: 'center', alignItems: 'center' }}>
				<Image
					source={require(`../assets/logo.png`)}
					style={{ width: 320, height: 150, borderWidth: 2, borderColor: 'red' }}
				/>
			</View>
			<View style={{ justifyContent: 'center', alignItems: 'center', marginTop: 20 }}>
				<TouchableOpacity>
					<View
						style={{
							alignItems: 'center',
							justifyContent: 'center',
							backgroundColor: '#1cd777',
							borderRadius: '8px',
							width: 200,
							height: 45
						}}
					>
						<Text style={{ color: 'white', fontWeight: 'bold' }}>دکمه</Text>
					</View>
				</TouchableOpacity>
			</View>

			<ScrollView pagingEnabled horizontal>
				{images.map((each) => {
					return (
						<Image
							source={{ uri: each }}
							style={{ width: '100%', height: 50, paddingHorizontal: 150, resizeMode: 'contain' }}
						/>
					);
				})}
			</ScrollView>
		</View>
	);
}

export default Home;
