import React, { Fragment } from 'react';
import { View, StatusBar, FlatList, Image, Dimensions } from 'react-native';

// Components
import EmptyBasket from './EmptyBasket';
import Border from '../Border';
import CustomButton from '../CustomButton';
import { colors } from '../../styles';
import { Context } from '../../Provider';
import CustomText from '../CustomText';

function CartContainer() {
	const { width } = Dimensions.get('window');
	return (
		<Context.Consumer>
			{(val) => {
				return (
					<View style={{ backgroundColor: 'white', flex: 1 }}>
						<StatusBar backgroundColor={colors.mainColor} />

						{val.cart.length ? (
							<Fragment>
								<View
									style={{
										justifyContent: 'space-between',
										marginHorizontal: 15,
										flexDirection: 'row',
										marginTop: 10
									}}
								>
									<CustomText customStyle={{ textAlign: 'left' }}>{val.cartTotal}</CustomText>
									<CustomText customStyle={{ textAlign: 'right' }}>مبلغ کل</CustomText>
								</View>
								<View
									style={{
										justifyContent: 'space-between',
										marginHorizontal: 15,
										flexDirection: 'row',
										marginTop: 10
									}}
								>
									<CustomText customStyle={{ textAlign: 'left' }}>{val.cartOffTotal}</CustomText>
									<CustomText customStyle={{ textAlign: 'right' }}>تخفیف کل</CustomText>
								</View>
								<View style={{ marginVertical: 10, alignSelf: 'center' }}>
									<CustomButton width={width - 20} text="تکمیل خرید و پرداخت" />
								</View>
								<View style={{ marginVertical: 15 }}>
									<Border />
								</View>
								<FlatList
									data={val.cart}
									renderItem={({ item }) => (
										<Image
											source={item.url}
											style={{ width: 200, height: 200, alignSelf: 'center' }}
										/>
									)}
								/>
							</Fragment>
						) : (
							<EmptyBasket />
						)}
					</View>
				);
			}}
		</Context.Consumer>
	);
}

export default CartContainer;
