import React, { Fragment, useEffect, useState } from 'react';
import { View, StatusBar, Image } from 'react-native';

import { colors } from '../../styles';

import { offerItems } from '../../images';

// Components
import CustomText from '../CustomText';

function LikeItem({ navigation, route }) {
	const [ item, setItem ] = useState(null);

	const { params } = route;

	useEffect(() => {
		const found = offerItems.find((any) => any.name === params);
		setItem(found);
		navigation.setOptions({
			title: found.name
		});
	}, []);

	return (
		<View style={{ backgroundColor: 'white', flex: 1 }}>
			<StatusBar backgroundColor={colors.mainColor} />
			{item && (
				<Fragment>
					<Image source={item.url} style={{ width: 200, height: 200, alignSelf: 'center' }} />
					<CustomText customStyle={{ marginRight: 10 }}>{item.name}</CustomText>
					<CustomText customStyle={{ marginRight: 10, color: 'grey' }}>{item.desc}</CustomText>
					<View style={{ flexDirection: 'row', justifyContent: 'space-between' }}>
						<CustomText customStyle={{ color: 'grey', textDecoration: 'line-through' }}>
							{item.offprice} تومان
						</CustomText>
						<CustomText customStyle={{ color: 'green' }}>{item.price} تومان</CustomText>
					</View>
				</Fragment>
			)}
		</View>
	);
}

export default LikeItem;
