import React from 'react';
import { ExpandableListView } from 'react-native-expandable-listview';
import { View, Text } from 'react-native';
const CONTENT = [
	{
		id: '42',

		categoryName: 'درباره بافردو',
		customItem: (
			<View style={{ flexDirection: 'column' }}>
				<Text>Custom Item</Text>
				<Text>With</Text>
				<Text>what you</Text>
				<Text>want</Text>
			</View>
		),

		subCategory: [
			{
				id: '1',
				name: 'ntialnged'
			}
		]
	},
	{
		id: '2',
		categoryName: 'شرایط بازگشت کالا',
		subCategory: [ { id: '1', name: 'Sub Item 1' } ]
	},
	{
		id: '94',
		categoryName: 'قوانین و مقررات',
		subCategory: [ { id: '1', name: 'Sub Item 1' } ]
	}
];
function List() {
	const handleItemClick = (e) => {
		console.log(e);
	};
	const handleInnerItemClick = (e) => {
		console.log(e);
	};
	return (
		<ExpandableListView
			data={CONTENT}
			onInnerItemClick={handleInnerItemClick}
			onItemClick={handleItemClick}
			ExpandableListViewStyles={{
				marginTop: -10
			}}
			renderItemSeparator={true}
			itemContainerStyle={{ backgroundColor: 'white' }}
			// customChevron={{}} // your custom image to the indicator
			chevronColor={'black'} // color of the default indicator
			innerItemContainerStyle={{ backgroundColor: 'white' }} // add your styles to all inner item containers of your list
			itemLabelStyle={{ marginLeft: 'auto', fontWeight: 'bold' }} // add your styles to all inner item text of your list
			// itemImageIndicatorStyle={{}} // add your styles to the image indicator of your list
			animated={false} // sets all animations on/off, default on
			innerItemLabelStyle={{ textAlign: 'right', paddingBottom: 10 }}

			// defaultLoaderStyles?: ViewStyle; // Set your styles to default loader (only for animated={true})
			// customLoader?: JSX.Element; Pass your custom loader, while your content is measured and rendered (only for animated={true})
		/>
	);
}

export default List;
