import React, { Fragment } from 'react';
import { View, Text, TouchableOpacity, Linking } from 'react-native';
import { colors } from '../styles';
import Button from './Button';

// Icons
import { FontAwesome5, FontAwesome, MaterialIcons, AntDesign, Feather } from '@expo/vector-icons';
import { ExpandableListView } from 'react-native-expandable-listview';

const CONTENT = [
	{
		id: '42',
		categoryName: 'درباره بافردو',
		subCategory: [
			{
				id: '1',
				name: 'ntialnged'
			}
		]
	},
	{
		id: '2',
		categoryName: 'شرایط بازگشت کالا',
		subCategory: [ { id: '1', name: 'Sub Item 1' } ]
	},
	{
		id: '94',
		categoryName: 'قوانین و مقررات',
		subCategory: [ { id: '1', name: 'Sub Item 1' } ]
	}
];

function Profile() {
	const handleItemClick = (e) => {
		console.log(e);
	};
	const handleInnerItemClick = (e) => {
		console.log(e);
	};
	return (
		<Fragment>
			<View style={{ backgroundColor: colors.mainColor, paddingVertical: 40 }} />
			<View
				style={{
					borderTopRightRadius: 30,
					borderTopLeftRadius: 30,
					paddingVertical: 40,
					backgroundColor: 'white'
				}}
			>
				<Text style={{ marginRight: 'auto', marginLeft: 'auto', width: 220, textAlign: 'center' }}>
					هم اکنون به جمع بافردو بپیوندید و از تخفیف ها زودتر از بقیه مطلع شوید.
				</Text>
				<View style={{ justifyContent: 'center', alignItems: 'center', marginTop: 20 }}>
					<Button width={200} text="ورود / ثبت نام" />
				</View>
			</View>

			<ExpandableListView
				data={CONTENT} // required
				onInnerItemClick={handleInnerItemClick}
				onItemClick={handleItemClick}
				ExpandableListViewStyles={{
					marginTop: 20
				}}
				renderItemSeparator={true}
				itemContainerStyle={{ backgroundColor: 'white' }} // add your styles to all item container of your list
				// customChevron={{}} // your custom image to the indicator
				// chevronColor="white" // color of the default indicator
				innerItemContainerStyle={{}} // add your styles to all inner item containers of your list
				itemLabelStyle={{ marginLeft: 'auto', fontWeight: 'bold' }} // add your styles to all inner item text of your list
				// itemImageIndicatorStyle={{}} // add your styles to the image indicator of your list
				animated={true} // sets all animations on/off, default on
				// defaultLoaderStyles?: ViewStyle; // Set your styles to default loader (only for animated={true})
				// customLoader?: JSX.Element; Pass your custom loader, while your content is measured and rendered (only for animated={true})
			/>

			<View
				style={{
					backgroundColor: 'white',
					borderTopRightRadius: 30,
					borderTopLeftRadius: 30,
					marginTop: 20,
					flexDirection: 'row',
					justifyContent: 'space-between',
					paddingHorizontal: 30,
					paddingVertical: 10
				}}
			>
				<TouchableOpacity onPress={() => Linking.openURL('https://telegram.me/@barishnews')}>
					<FontAwesome5 name="telegram-plane" size={24} color="black" />
				</TouchableOpacity>
				<TouchableOpacity onPress={() => Linking.openURL('https://wa.me/989371101609')}>
					<FontAwesome name="whatsapp" size={24} color="black" />
				</TouchableOpacity>
				<TouchableOpacity onPress={() => Linking.openURL('https://www.instagram.com/vionaapp.ir/')}>
					<AntDesign name="instagram" size={24} color="black" />
				</TouchableOpacity>
				<TouchableOpacity onPress={() => Linking.openURL('mailto:vionaapp@example.com')}>
					<MaterialIcons name="email" size={24} color="black" />
				</TouchableOpacity>
				<TouchableOpacity onPress={() => Linking.openURL('tel:09371101609')}>
					<Feather name="phone-call" size={24} color="black" />
				</TouchableOpacity>
			</View>
			<Text style={{ textAlign: 'center', marginTop: 10 }}> v1 </Text>
		</Fragment>
	);
}

export default Profile;
