import React, { Component } from 'react';
export const Context = React.createContext();

export class Provider extends Component {
	state = {
		name: '',
		number: '',
		liked: []
	};

	changeName = (e) => {
		this.setState({
			name: e
		});
	};

	changeNumber = (e) => {
		this.setState({
			number: e
		});
	};

	like = (newLike) => {
		this.setState({
			liked: [ ...this.state.liked, newLike ]
		});
	};

	remove = (itemName) => {
		const filtered = this.state.liked.filter((any) => any.name === itemName);
		console.log(filtered);
		this.setState({
			liked: filtered
		});
	};

	render() {
		return (
			<Context.Provider
				value={{
					changeNumber: this.changeNumber,
					changeName: this.changeName,
					name: this.state.name,
					number: this.state.number,
					liked: this.state.liked,
					like: this.like,
					remove: this.remove
				}}
			>
				{this.props.children}
			</Context.Provider>
		);
	}
}

export default Provider;
