import React, { Fragment } from 'react';
import { FlatList, Image, View, Text } from 'react-native';
import { offerItems } from '../images';

// Components
import CustomText from './CustomText';
import { colors } from '../styles';

function Offers() {
	return (
		<Fragment>
			<CustomText customStyle={{ textAlign: 'right', marginRight: 25, fontSize: 25, marginTop: 15 }}>
				پیشنهاد ویژه
			</CustomText>
			<View
				style={{
					borderBottomColor: 'black',
					borderBottomWidth: 1
				}}
			/>
			<FlatList
				data={offerItems}
				horizontal={true}
				renderItem={({ item }) => (
					<View style={{ marginTop: 18 }}>
						<View
							style={{
								marginRight: 40,
								marginLeft: 20
							}}
						>
							<Image
								source={item.url}
								style={{
									width: 260,
									height: 220,
									resizeMode: 'cover'
								}}
							/>
							<View
								style={{
									position: 'absolute',
									top: 10,
									left: 0,
									backgroundColor: colors.secondaryColor,
									borderTopRightRadius: 15,
									borderBottomRightRadius: 15
								}}
							>
								<CustomText customStyle={{ color: 'white', padding: 10 }}>{item.off}</CustomText>
							</View>
							<CustomText customStyle={{ marginTop: 10 }}>{item.name}</CustomText>
							<CustomText customStyle={{ marginTop: 7, color: 'grey' }}>{item.desc}</CustomText>
							<View style={{ flexDirection: 'row', justifyContent: 'space-between', marginTop: 10 }}>
								<CustomText customStyle={{ color: 'green' }}>{item.price} تومان</CustomText>
								<CustomText
									customStyle={{
										color: 'grey',
										textDecorationLine: 'line-through',
										textDecorationColor: 'blue'
									}}
								>
									{item.offprice} تومان
								</CustomText>
							</View>
						</View>
					</View>
				)}
			/>

			<Image
				source={require('../assets/three.png')}
				style={{ width: 320, height: 270, alignSelf: 'center', marginBottom: 30, marginTop: 30 }}
			/>
		</Fragment>
	);
}

export default Offers;
