import React, { Fragment, useState } from 'react';
import { View, Dimensions, ScrollView, Image } from 'react-native';
import { images } from '../images';
import { globalStyles } from '../styles';

function Carousel() {
	const [ num, setNum ] = useState(0);
	const { width } = Dimensions.get('window');
	const handle = ({ nativeEvent }) => {
		const slide = Math.ceil(nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width);
		if (slide !== num && slide < images.length) {
			setNum(slide);
		}
	};
	return (
		<Fragment>
			<ScrollView
				style={{ marginTop: 20 }}
				onScroll={handle}
				horizontal
				pagingEnabled
				showsHorizontalScrollIndicator={false}
			>
				{images.map((each, index) => {
					return (
						<Image
							key={index}
							source={each.url}
							style={{ width: width, height: 120, resizeMode: 'contain', borderRadius: 8 }}
						/>
					);
				})}
			</ScrollView>

			<View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center', marginTop: 30 }}>
				{images.map((each, index) => {
					return (
						<View
							style={{
								backgroundColor: index === num ? globalStyles.mainColor : '#dddddd',
								width: 50,
								height: 8,
								marginHorizontal: 10,
								borderRadius: 30
							}}
						/>
					);
				})}
			</View>
		</Fragment>
	);
}

export default Carousel;
