import React, { Component } from 'react';
import * as Font from 'expo-font';

// Components
import Home from './components/Home';
import Profile from './components/Profile';

// Navigation
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

import { AntDesign } from '@expo/vector-icons';
import { colors } from './styles';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Tab = createBottomTabNavigator();

export class App extends Component {
	componentDidMount() {
		Font.loadAsync({
			Vazir: require('./assets/font/Vazir.ttf')
		});
		AsyncStorage.clear();
	}
	render() {
		return (
			<NavigationContainer>
				<Tab.Navigator
					initialRouteName="Profile"
					tabBarOptions={{
						activeTintColor: colors.mainColor,
						style: { height: 60 },
						labelStyle: { marginBottom: 10, fontWeight: 'bold', fontSize: 10.5, fontFamily: 'Vazir' }
					}}
				>
					<Tab.Screen
						options={{
							title: 'پروفایل',
							tabBarIcon: ({ color, size }) => <AntDesign name="user" size={size} color={color} />
						}}
						component={Profile}
						name="Profile"
					/>
					<Tab.Screen
						options={{
							title: 'علاقه مندی ها',
							tabBarIcon: ({ color, size }) => <AntDesign name="hearto" size={size} color={color} />
						}}
						component={Home}
						name="Favourites"
					/>
					<Tab.Screen
						options={{
							title: 'سبد خرید',
							tabBarIcon: ({ color, size }) => <AntDesign name="shoppingcart" size={size} color={color} />
						}}
						component={Home}
						name="Cart"
					/>
					<Tab.Screen
						options={{
							title: 'جستجو و دسته بندی',
							tabBarIcon: ({ color, size }) => <AntDesign name="search1" size={size} color={color} />
						}}
						component={Home}
						name="Search"
					/>
					<Tab.Screen
						options={{
							title: 'خانه',
							tabBarIcon: ({ color, size }) => <AntDesign name="home" size={size} color={color} />
						}}
						component={Home}
						name="Home"
					/>
				</Tab.Navigator>
			</NavigationContainer>
		);
	}
}

export default App;
