import React from 'react';
import { View, Text, FlatList, Image } from 'react-native';
import { images } from '../images';
function Items() {
	return (
		<View style={{ marginVertical: 30 }}>
			<FlatList
				numColumns={3}
				data={images}
				renderItem={({ item }) => (
					<View style={{ backgroundColor: 'red', alignItems: 'center', justifyContent: 'space-between' }}>
						<Image style={{ position: 'relative', width: 40, height: 140 }} source={item.url} />

						<Text style={{ position: 'absolute', bottom: 0, backgroundColor: 'red' }}>{item.name}</Text>
					</View>
				)}
			/>
		</View>
	);
}

export default Items;
