import React from 'react';
import { View, Text, FlatList, Image, Dimensions } from 'react-native';
import { images } from '../images';
import { colors } from '../styles';
function Items() {
	const { width } = Dimensions.get('window');
	return (
		<View
			style={{
				marginHorizontal: 10
			}}
		>
			<View
				style={{
					justifyContent: 'space-between',
					alignItems: 'center'
				}}
			>
				<FlatList
					numColumns={3}
					data={images}
					renderItem={({ item }) => (
						<View
							style={{
								borderRadius: 8,

								marginHorizontal: 20
							}}
						>
							<Image style={{ position: 'relative', width: 240, height: 140 }} source={item.url} />
							<View
								style={{
									position: 'absolute',
									bottom: 0,
									alignSelf: 'center',
									backgroundColor: colors.secondaryColor,
									width: '100%',
									borderBottomRightRadius: 8,
									borderBottomLeftRadius: 98
								}}
							>
								<Text
									style={{
										color: 'white',
										// backgroundColor: 'blue',
										marginLeft: 'auto',
										marginRight: 'auto'
									}}
								>
									{item.name}
								</Text>
							</View>
						</View>
					)}
				/>
			</View>
		</View>
	);
}

export default Items;
