import React from 'react';
import { View, Text, Image } from 'react-native';

function UserInfo({ name, number }) {
	return (
		<View
			style={{
				backgroundColor: 'white',
				borderTopRightRadius: 20,
				borderTopLeftRadius: 20,
				marginTop: -100,
				paddingBottom: 50
			}}
		>
			<Image source={require('../assets/user.png')} style={{ width: 50, height: 50, alignSelf: 'center' }} />
			<Text style={{ textAlign: 'center', fontFamily: 'Vazir', fontWeight: 'bold' }}>{name} کاربر</Text>
			<Text style={{ textAlign: 'center', marginTop: 10, fontFamily: 'Vazir', fontWeight: 'bold' }}>
				{number}
			</Text>
		</View>
	);
}

export default UserInfo;
