import React, { Fragment } from 'react';
import { FlatList, Image, View, Text } from 'react-native';
import { offerItems } from '../images';

// Components
import CustomText from './CustomText';

function Offers() {
	return (
		<Fragment>
			<CustomText customStyle={{ textAlign: 'right', marginRight: 25, fontSize: 25, marginTop: 15 }}>
				پیشنهاد ویژه
			</CustomText>

			<FlatList
				data={offerItems}
				horizontal={true}
				renderItem={({ item }) => (
					<View style={{ marginTop: 18 }}>
						<View
							style={{
								marginRight: 40,
								marginLeft: 20
							}}
						>
							<Image
								source={item.url}
								style={{
									width: 260,
									height: 220,
									resizeMode: 'cover'
								}}
							/>
							<View style={{ position: 'absolute', top: 0, left: 0 }}>
								<CustomText>ss</CustomText>
							</View>
						</View>
					</View>
				)}
			/>

			<Image
				source={require('../assets/three.png')}
				style={{ width: 320, height: 270, alignSelf: 'center', marginBottom: 30, marginTop: 30 }}
			/>
		</Fragment>
	);
}

export default Offers;
