import React from 'react';
import { View, Text, FlatList, Image, Dimensions } from 'react-native';
import { images } from '../images';
import { colors } from '../styles';
function Items() {
	const { width } = Dimensions.get('window');
	return (
		<View
			style={{
				alignItems: 'center'
			}}
		>
			<FlatList
				numColumns={3}
				data={images}
				renderItem={({ item }) => (
					<View
						style={{
							borderRadius: 8,
							marginHorizontal: 8,
							marginVertical: 8
						}}
					>
						<Image style={{ position: 'relative', width: 114, height: 110 }} source={item.url} />
						<View
							style={{
								position: 'absolute',
								bottom: 0,
								backgroundColor: colors.secondaryColor,
								width: '100%',
								borderBottomRightRadius: 8,
								borderBottomLeftRadius: 8,
								opacity: 0.8
							}}
						>
							<Text
								style={{
									color: 'white',
									marginLeft: 'auto',
									marginRight: 'auto'
								}}
							>
								{item.name}
							</Text>
						</View>
					</View>
				)}
			/>
		</View>
	);
}

export default Items;
