import React from 'react';
import { View, Text, Image } from 'react-native';

function UserInfo({ name, number }) {
	return (
		<View style={{ backgroundColor: 'white', borderTopRightRadius: 8, borderTopLeftRadius: 8 }}>
			<Image source={require('../assets/user.png')} style={{ width: 50, height: 50, alignSelf: 'center' }} />
			<Text style={{ textAlign: 'center' }}>{name} کاربر</Text>
			<Text style={{ textAlign: 'center' }}>{number}</Text>
		</View>
	);
}

export default UserInfo;
