import React from 'react';
import { ScrollView, Text, TouchableOpacity, View } from 'react-native';
import { Context } from '../Provider';
import { EvilIcons } from '@expo/vector-icons';
import { globalStyles } from '../styles';

// Components
import Social from './Social';
import ProfileTop from './ProfileTop';
import ProfileInfo from './ProfileInfo';
import UserInfo from './UserInfo';
import List from './List';

function ProfileContainer({ navigation }) {
	const logout = (val) => {
		val.changeName('');
		val.changeNumber('');
	};
	return (
		<Context.Consumer>
			{(val) => {
				return (
					<ScrollView>
						{val.name.length ? (
							<TouchableOpacity style={globalStyles.closeIcon} onPress={() => logout(val)}>
								<View style={{ flexDirection: 'row', alignItems: 'center' }}>
									<EvilIcons name="close" size={24} color="white" />
									<Text style={{ marginLeft: 10, color: '#eeeeee', fontFamily: 'Vazir' }}>
										خروج از حساب
									</Text>
								</View>
							</TouchableOpacity>
						) : null}
						<ProfileTop />
						{val.name.length ? (
							<UserInfo name={val.name} number={val.number} />
						) : (
							<ProfileInfo navigation={navigation} />
						)}
						<List />
						<Social />
					</ScrollView>
				);
			}}
		</Context.Consumer>
	);
}

export default ProfileContainer;
