import React from 'react';
import { View, StatusBar, FlatList, Text, Button, Image } from 'react-native';
import { Context } from '../../Provider';

import { colors } from '../../styles';
import CustomText from '../CustomText';

// Components
import EmptyLikes from './EmptyLikes';

function LikeContainer() {
	return (
		<Context.Consumer>
			{(val) => {
				return (
					<View style={{ justifyContent: 'center', alignItems: 'center', backgroundColor: 'white', flex: 1 }}>
						<StatusBar backgroundColor={colors.mainColor} />

						{val.liked.length ? (
							<FlatList
								numColumns={2}
								data={val.liked}
								renderItem={({ item }) => (
									<View
										style={{
											marginHorizontal: 10,
											marginVertical: 10,
											alignItems: 'center',
											justifyContent: 'center',
											borderWidth: 1,
											borderColor: '#f4f4f4',
											padding: 10,
											borderRadius: 10
										}}
									>
										<Image style={{ width: 200, height: 120 }} source={item.url} />
										<CustomText customStyle={{ marginTop: 10 }}>{item.name}</CustomText>
										<CustomText customStyle={{ color: 'grey', marginVertical: 10 }}>
											{item.desc}
										</CustomText>
										<View
											style={{
												flexDirection: 'row',
												justifyContent: 'space-between',
												borderWidth: 2,
												borderColor: 'red',
												width: 200
											}}
										>
											<CustomText
												customStyle={{
													color: 'grey',
													textDecorationLine: 'line-through'
												}}
											>
												{item.offprice} تومان
											</CustomText>
											<CustomText
												customStyle={{
													color: 'green'
												}}
											>
												{item.offprice} تومان
											</CustomText>
										</View>
									</View>
								)}
							/>
						) : (
							<EmptyLikes />
						)}

						<Button
							onPress={() =>
								val.like({
									name: 'هودی Aroma',
									url: require('../../assets/products/hoodie.png'),
									price: '۵۴۸,۰۰۰',
									offprice: '۶۸۵,۰۰۰',
									desc: `سویشرت یقه گرد مردانه آروما Aroma کد 12501024`,
									off: '۲۰%',
									key: '1',
									liked: false,
									inCart: false
								})}
							title="Add to liked"
						/>
					</View>
				);
			}}
		</Context.Consumer>
	);
}

export default LikeContainer;
