import React from 'react';
import { View, Text, Image, TouchableOpacity, ScrollView, Dimensions, SafeAreaView } from 'react-native';

const images = [
	{ name: 'imgone', url: require('../assets/logo.png') },
	{ name: 'imgtwo', url: require('../assets/logo.png') },
	{ name: 'imgthree', url: require('../assets/logo.png') },
	{ name: 'imgfour', url: require('../assets/logo.png') }
];

function Home() {
	const [ num, setNum ] = React.useState(0);
	const { width } = Dimensions.get('window');
	const handle = ({ nativeEvent }) => {
		const slide = Math.ceil(nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width);
		if (slide !== num && slide < images.length) {
			setNum(slide);
		}
	};

	return (
		<View style={{ flex: 1, backgroundColor: 'white' }}>
			<View>
				<View style={{ justifyContent: 'center', alignItems: 'center' }}>
					<Image
						source={require(`../assets/logo.png`)}
						style={{ width: 320, height: 150, borderWidth: 2, borderColor: 'red' }}
					/>
				</View>
				<View style={{ justifyContent: 'center', alignItems: 'center', marginTop: 20 }}>
					<TouchableOpacity>
						<View
							style={{
								alignItems: 'center',
								justifyContent: 'center',
								backgroundColor: '#1cd777',
								borderRadius: 8,
								width: 200,
								height: 45
							}}
						>
							<Text style={{ color: 'white', fontWeight: 'bold' }}>دکمه</Text>
						</View>
					</TouchableOpacity>
				</View>

				<ScrollView
					style={{ marginTop: 20 }}
					onScroll={handle}
					horizontal
					pagingEnabled
					showsHorizontalScrollIndicator={false}
				>
					{images.map((each, index) => {
						return (
							<Image
								key={index}
								source={each.url}
								style={{ width: width, height: 220, resizeMode: 'contain', borderRadius: 8 }}
							/>
						);
					})}
				</ScrollView>

				<View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center', marginTop: 30 }}>
					{images.map((each, index) => {
						return (
							<View
								style={{
									backgroundColor: index === num ? '#1cd777' : '#dddddd',
									width: 50,
									height: 8,
									marginHorizontal: 10,
									borderRadius: 30
								}}
							/>
						);
					})}
				</View>
			</View>
		</View>
	);
}

export default Home;
