import React from 'react';
import { View, Text, Image } from 'react-native';

function UserInfo({ name, number }) {
	return (
		<View
			style={{
				borderTopRightRadius: 30,
				borderTopLeftRadius: 30,
				paddingVertical: 40,
				backgroundColor: 'white',
				transform: [ { translateY: -25 } ]
			}}
		>
			<Image
				source={require('../assets/user.png')}
				style={{ width: 80, height: 80, alignSelf: 'center', marginTop: 20 }}
			/>
			<Text style={{ textAlign: 'center', fontFamily: 'Vazir', fontWeight: 'bold', fontSize: 25 }}>
				{name} کاربر
			</Text>
			<Text style={{ textAlign: 'center', marginTop: 6, fontFamily: 'Vazir', fontWeight: 'bold' }}>{number}</Text>
		</View>
	);
}

export default UserInfo;
