import React from 'react';
import { View, TextInput, Image } from 'react-native';
import Button from './Button';

function Input({ navigation }) {
	const submit = () => {
		// Async
		navigation.goBack();
	};
	return (
		<View
			style={{
				borderTopRightRadius: 30,
				borderTopLeftRadius: 30,
				paddingTop: 40,
				paddingBottom: 25,
				backgroundColor: 'white',
				flex: 1,
				marginTop: -25
			}}
		>
			<View style={{ alignSelf: 'center' }}>
				<Image style={{ width: 240, height: 100 }} source={require('../assets/logo.png')} />
			</View>
			<View style={{ marginTop: 20, marginRight: 'auto', marginLeft: 'auto', width: 220, textAlign: 'center' }}>
				<TextInput
					style={{
						direction: 'rtl',
						backgroundColor: '#eeeeee',
						borderRadius: 9,
						height: 45,
						marginBottom: 10,
						padding: 10,
						fontFamily: 'Vazir'
					}}
					onChangeText={(e) => console.log(e)}
					placeholder="شماره موبایل ..."
				/>
				<Button width={220} onPress={submit} text="ورود به حساب" />
			</View>

			{/* <View style={{ justifyContent: 'center', alignItems: 'center', marginTop: 20 }}>
				<Button onPress={navigate} width={200} text="ورود / ثبت نام" />
			</View> */}
		</View>
	);
}

export default Input;
