import React, { Fragment } from 'react';
import { FlatList, View } from 'react-native';
import { offerItems } from '../images';

// Components
import CustomText from './CustomText';
import ItemDetails from './ItemDetails';
import Border from './Border';
import ImageOff from './ImageOff';
import { colors } from '../styles';

function Offers() {
	return (
		<Fragment>
			<CustomText customStyle={{ textAlign: 'right', marginRight: 25, fontSize: 25, marginTop: 15 }}>
				پیشنهاد ویژه
			</CustomText>
			<Border />
			<FlatList
				data={offerItems}
				horizontal={true}
				renderItem={({ item }) => (
					<View style={{ marginTop: 18 }}>
						<View
							style={{
								marginRight: 40,
								marginLeft: 20
							}}
						>
							<ImageOff item={item} />
							<ItemDetails item={item} />
						</View>
					</View>
				)}
			/>
		</Fragment>
	);
}

export default Offers;
