import React, { Fragment } from 'react';
import { FlatList, Image, View } from 'react-native';
import { offerItems } from '../images';

// Components
import CustomText from './CustomText';

function Offers() {
	return (
		<Fragment>
			<CustomText customStyle={{ textAlign: 'right', marginRight: 25, fontSize: 25, marginTop: 15 }}>
				پیشنهاد ویژه
			</CustomText>

			<FlatList
				data={offerItems}
				horizontal={true}
				renderItem={({ item }) => (
					<View>
						<Image
							source={item.url}
							style={{ width: 320, height: 260, marginRight: 40, resizeMode: 'contain' }}
						/>
					</View>
				)}
			/>

			<Image
				source={require('../assets/three.png')}
				style={{ width: 320, height: 270, alignSelf: 'center', marginBottom: 30, marginTop: 30 }}
			/>
		</Fragment>
	);
}

export default Offers;
