import React from 'react';
import { View, Text, Image, TouchableOpacity, ScrollView, Dimensions, SafeAreaView } from 'react-native';

const images = [
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg',
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg',
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg',
	'https://www.cbronline.com/wp-content/uploads/2016/06/what-is-URL-770x503.jpg'
];

function Home() {
	const [ num, setNum ] = React.useState(0);
	const { width } = Dimensions.get('window');
	const handle = ({ nativeEvent }) => {
		const slide = Math.ceil(nativeEvent.contentOffset.x / nativeEvent.layoutMeasurement.width);
		if (slide !== num && slide < 4) {
			setNum(slide);
		}
	};
	return (
		<SafeAreaView style={{ backgroundColor: 'white', flex: 1 }}>
			<View style={{ justifyContent: 'center', alignItems: 'center' }}>
				<Image
					source={require(`../assets/logo.png`)}
					style={{ width: 320, height: 150, borderWidth: 2, borderColor: 'red' }}
				/>
			</View>
			<View style={{ justifyContent: 'center', alignItems: 'center', marginTop: 20 }}>
				<TouchableOpacity>
					<View
						style={{
							alignItems: 'center',
							justifyContent: 'center',
							backgroundColor: '#1cd777',
							borderRadius: 8,
							width: 200,
							height: 45
						}}
					>
						<Text style={{ color: 'white', fontWeight: 'bold' }}>دکمه</Text>
					</View>
				</TouchableOpacity>
			</View>

			<ScrollView onScroll={handle} horizontal pagingEnabled showsHorizontalScrollIndicator={false}>
				{images.map((each, index) => {
					return <Image key={index} source={{ uri: each }} style={{ width: width, height: 120 }} />;
				})}
			</ScrollView>
			{images.map((each, index) => {
				return (
					<View style={{ backgroundColor: index === num ? 'red' : 'blue' }}>
						<Text>scroll</Text>
					</View>
				);
			})}
		</SafeAreaView>
	);
}

export default Home;
