import axios from 'axios';
import React, { Component, Fragment } from 'react';
import { FlatList, View, Text } from 'react-native';

export class App extends Component {
	state = {
		data: []
	};
	componentDidMount() {
		axios
			.get('https://jsonplaceholder.typicode.com/posts?_page=1')
			.then((res) => {
				this.setState({
					data: res.data
				});
			})
			.catch((e) => {
				console.log(e.message);
			});
	}

	handle = () => {
		console.log('this ran');
		axios
			.get('https://jsonplaceholder.typicode.com/posts?_page=2')
			.then((res) => {
				this.setState({ data: [ ...this.state.data, res.data ] });
			})
			.catch((e) => {
				console.log(e.message);
			});
	};
	render() {
		return (
			<Fragment>
				<FlatList
					data={this.state.data}
					keyExtractor={(each) => each.id}
					onEndReachedThreshold={0}
					onEndReached={this.handle}
					renderItem={({ item }) => (
						<View style={{ paddingVertical: 200 }}>
							<Text>{item.title}</Text>
						</View>
					)}
				/>
				<View>
					<Text>yo</Text>
				</View>
			</Fragment>
		);
	}
}

export default App;
