import React from 'react';
import { View, StatusBar, FlatList, Text, Button, Image } from 'react-native';
import { Context } from '../../Provider';

import { colors } from '../../styles';

// Components
import EmptyLikes from './EmptyLikes';

function LikeContainer() {
	return (
		<Context.Consumer>
			{(val) => {
				return (
					<View style={{ justifyContent: 'center', alignItems: 'center', backgroundColor: 'white', flex: 1 }}>
						<StatusBar backgroundColor={colors.mainColor} />

						{val.liked.length ? (
							<FlatList
								numColumns={2}
								data={val.liked}
								renderItem={({ item }) => (
									<View style={{ marginHorizontal: 10, marginVertical: 10 }}>
										<Image style={{ width: 200, height: 120 }} source={item.url} />
										<Text>{item.name}</Text>
									</View>
								)}
							/>
						) : (
							<EmptyLikes />
						)}

						<Button
							onPress={() =>
								val.like({
									name: 'هودی Aroma',
									url: require('../../assets/products/hoodie.png'),
									price: '۵۴۸,۰۰۰',
									offprice: '۶۸۵,۰۰۰',
									desc: `سویشرت یقه گرد مردانه آروما Aroma کد 12501024`,
									off: '۲۰%',
									key: '1',
									liked: false,
									inCart: false
								})}
							title="Add to liked"
						/>
					</View>
				);
			}}
		</Context.Consumer>
	);
}

export default LikeContainer;
